import sysfrom pprint import PrettyPrinterimport pandas as pddef visual_check(result, question):    PrettyPrinter(indent=4).pprint(result)    print(question, "Then press enter ...")    sys.stdin.readline()def columns_diff(df, df_temp):    print("\nThe following new columns have beed added: ", set(df_temp.columns).difference(set(df.columns)))    print("\nThe following columns have been removed: ", set(df.columns).difference(set(df_temp.columns)))def new_column_diff(df, column, column_new, aggregate=True):    print("\nThe following rows have been altered in place:" )    changes = {}    for key, value in df[column].iteritems():        replace = (str(df.loc[key, column_new]).replace(str(value), ''))        if replace != '':            changes[key] = f"{value} => {replace}"        PrettyPrinter(indent=4).pprint(pd.Series(changes).value_counts().to_dict())def same_column_diff(df, df_temp, column, aggregate=True):    print("\nNew columns have the following individual changes:" )    changes = {}    for key, value in df[column].iteritems():        replace = ( str(df_temp.loc[key,column]).replace(str(value), ''))        if replace != '':            changes[key] = f"{value} => {replace}"        PrettyPrinter(indent=4).pprint(pd.Series(changes).value_counts().to_dict())